// frontend/src/App.js

import React, { useState } from 'react';
import DropdownMenu from './components/DropdownMenu';
import AssetForm from './components/AssetForm';
import AssetListPanel from './components/AssetListPanel';
import './styles/App.css';

const App = () => {
    const [savedAssets, setSavedAssets] = useState([]);
    const [selectedAssets, setSelectedAssets] = useState([]);
    const [fetchedControls, setFetchedControls] = useState([]);
    const [controlsPerAsset, setControlsPerAsset] = useState({}); // Track selected controls for each asset

    // Handle asset selection
    const handleSelectAsset = (assetName) => {
        setSelectedAssets(prev => {
            if (!prev.includes(assetName)) {
                return [...prev, assetName];
            }
            return prev;
        });
    };

    // Fetch and update available controls for the selected asset
    const handleControlsFetched = (controls) => {
        setFetchedControls(controls); // Set available controls for the current asset
        setControlsPerAsset(prev => {
            const lastSelectedAsset = selectedAssets[selectedAssets.length - 1];
            if (lastSelectedAsset && !(lastSelectedAsset in prev)) {
                return { ...prev, [lastSelectedAsset]: [] }; // Initialize controls for new asset selection
            }
            return prev;
        });
    };

    // Toggle control selection for each asset independently
    const handleControlSelection = (assetName, control) => {
        setControlsPerAsset(prev => {
            const currentControls = prev[assetName] || [];
            const updatedControls = currentControls.includes(control)
                ? currentControls.filter(c => c !== control) // Remove if already selected
                : [...currentControls, control]; // Add if not selected
            return { ...prev, [assetName]: updatedControls };
        });
    };

    // Save asset data including both selected (existing) and unselected (additional) controls
    const handleSaveAsset = (assetData) => {
        if (selectedAssets.length > 0) {
            const assetsToSave = selectedAssets.map(asset => {
                const selectedControls = controlsPerAsset[asset] || []; // Selected controls for this asset
                const allAvailableControls = fetchedControls; // Available controls fetched for this asset

                // Calculate additionalControls as controls in fetchedControls that are not in selectedControls
                const unselectedControls = allAvailableControls.filter(
                    control => !selectedControls.includes(control)
                );

                return {
                    assetName: asset,
                    actionOwner: assetData.actionOwner,
                    riskIdentificationDate: assetData.riskIdentificationDate,
                    mitigationTimeline: "10-15 Days from Risk Identification Date",
                    existingControls: selectedControls, // Store selected controls
                    additionalControls: unselectedControls // Store unselected controls
                };
            });

            // Update saved assets and clear selected states
            setSavedAssets(prevAssets => [...prevAssets, ...assetsToSave]);
            setSelectedAssets([]); // Clear selected assets after saving
            setControlsPerAsset({}); // Clear control selection for all assets after saving
            console.log("Assets saved:", assetsToSave);
        } else {
            console.warn("Please select at least one asset.");
        }
    };

    const handleGenerateReport = async () => {
        console.log("Generate Report button clicked");

        try {
            const response = await fetch('http://localhost:5000/api/reports/generate-report', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ assetList: savedAssets })
            });

            if (response.ok) {
                const result = await response.json();
                console.log("Report generated successfully:", result.reportUrl);
                window.open(result.reportUrl, '_blank');
            } else {
                const result = await response.json();
                console.error('Failed to generate report:', result.message);
            }
        } catch (error) {
            console.error('Error generating report:', error);
        }
    };

    return (
        <div
            className="background"
            style={{
                backgroundImage: `url(${process.env.PUBLIC_URL + '/CySecAssureBG.jpeg'})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                backgroundRepeat: 'no-repeat',
                backgroundAttachment: 'fixed',
                width: '100vw',
                height: '100vh',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                overflow: 'hidden',
            }}
        >
            <div className="overlay">
                <div className="left-panel">
                    <DropdownMenu
                        selectedAssets={selectedAssets}
                        onSelectAsset={handleSelectAsset}
                        onControlsFetched={handleControlsFetched}
                    />
                    <AssetForm onSave={handleSaveAsset} />
                </div>
                <div className="right-panel">
                    <h3>Available Controls</h3>
                    {fetchedControls.length > 0 ? (
                        fetchedControls.map(control => (
                            <div key={control} className="control-option">
                                <input
                                    type="checkbox"
                                    checked={(controlsPerAsset[selectedAssets[selectedAssets.length - 1]] || []).includes(control)}
                                    onChange={() => handleControlSelection(selectedAssets[selectedAssets.length - 1], control)}
                                />
                                <label>{control}</label>
                            </div>
                        ))
                    ) : (
                        <p>Select an asset to see controls</p>
                    )}
                    <AssetListPanel
                        savedAssets={savedAssets}
                        onUpdateAssetControls={(assetName, controls) => {
                            setSavedAssets(prev =>
                                prev.map(a =>
                                    a.assetName === assetName
                                        ? { ...a, existingControls: controls }
                                        : a
                                )
                            );
                        }}
                    />
                    <div className="generate-report-container">
                        <button onClick={handleGenerateReport} className="generate-report-button">
                            Generate Report
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default App;
