// frontend/src/App.js
import React, { useState } from 'react';
import DropdownMenu from './components/DropdownMenu';
import AssetForm from './components/AssetForm';
import AssetListPanel from './components/AssetListPanel';
import './styles/App.css';

const App = () => {
  const [savedAssets, setSavedAssets] = useState([]);
  const [lastAssetData, setLastAssetData] = useState(null);

  // Handle saving an asset temporarily
  const handleSaveAsset = (assetData) => {
    console.log("Saving asset:", assetData); // Debugging: log asset data
    setSavedAssets((prevAssets) => {
      const updatedAssets = [...prevAssets, assetData];
      console.log("Updated saved assets:", updatedAssets); // Debugging: log updated saved assets
      return updatedAssets;
    });
    setLastAssetData(assetData); // Update last asset data for 'Same As Previous'
  };

  return (
    <div
      className="background"
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL + '/CySecAssureBG.jpeg'})`,
        backgroundSize: 'cover',
        backgroundAttachment: 'fixed',
        width: '100vw',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
      }}
    >
      <div className="overlay">
        <div className="left-panel">
          <DropdownMenu onSelectAsset={(selectedAsset) => setLastAssetData(prev => ({ ...prev, selectedAsset }))} />
          <AssetForm onSave={handleSaveAsset} lastAssetData={lastAssetData} />
        </div>

        <div className="right-panel">
          <AssetListPanel savedAssets={savedAssets} />
        </div>

        {/* Bottom row for buttons */}
        <div className="button-row">
          <button className="add-asset-button" onClick={() => handleSaveAsset(lastAssetData)}>Add Asset</button>
          <button className="generate-report-button">Generate Report</button>
        </div>
      </div>
    </div>
  );
};

export default App;
