// ExistingControlsPopup.js (new component)
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ExistingControlsPopup = ({ assetName, onClose, onSave }) => {
    const [controls, setControls] = useState([]);
    const [selectedControls, setSelectedControls] = useState([]);

    useEffect(() => {
        // Fetch existing controls from backend when component mounts
        axios.get(`/api/assets/${assetName}/controls`)
            .then(response => setControls(response.data.controls.split('\n')))
            .catch(error => console.error("Error fetching controls:", error));
    }, [assetName]);

    const toggleControl = (control) => {
        setSelectedControls(prev =>
            prev.includes(control)
                ? prev.filter(c => c !== control)
                : [...prev, control]
        );
    };

    const handleSave = () => {
        onSave(selectedControls); // Pass selected controls to parent
        onClose(); // Close the popup
    };

    return (
        <div className="popup-overlay">
            <div className="popup-content">
                <h3>Select Existing Controls for {assetName}</h3>
                {controls.map((control, index) => (
                    <div key={index}>
                        <input
                            type="checkbox"
                            checked={selectedControls.includes(control)}
                            onChange={() => toggleControl(control)}
                        />
                        <label>{control}</label>
                    </div>
                ))}
                <button onClick={handleSave}>Save</button>
                <button onClick={onClose}>Cancel</button>
            </div>
        </div>
    );
};

export default ExistingControlsPopup;
