// frontend/src/components/DropdownMenu.js
import React, { useState, useEffect } from 'react';
import { fetchCollectionsWithAssets } from '../services/apiService';

const DropdownMenu = ({ selectedAssets, onSelectAsset }) => {
    const [assets, setAssets] = useState([]);
    const [isOpen, setIsOpen] = useState(false);

    const toggleDropdown = () => setIsOpen(prev => !prev);

    useEffect(() => {
        const loadAssets = async () => {
            const data = await fetchCollectionsWithAssets();
            const assetNames = data.flatMap(collection => collection.assets);
            console.log("Assets loaded into DropdownMenu:", assetNames);
            setAssets(assetNames);
        };
        loadAssets();
    }, []);

    const handleAssetClick = (assetName) => {
        if (selectedAssets.includes(assetName)) {
            onSelectAsset(selectedAssets.filter(asset => asset !== assetName));
        } else {
            onSelectAsset([...selectedAssets, assetName]);
        }
    };

    return (
        <div className="dropdown-menu">
            <button onClick={toggleDropdown}>Select Assets</button>
            {isOpen && (
                <div className="dropdown-list">
                    {assets.length > 0 ? (
                        assets.map((asset) => (
                            <div key={asset} className="dropdown-item">
                                <input
                                    type="checkbox"
                                    checked={selectedAssets.includes(asset)}
                                    onChange={() => handleAssetClick(asset)}
                                />
                                <label>{asset}</label>
                            </div>
                        ))
                    ) : (
                        <p>No assets available</p>
                    )}
                </div>
            )}
        </div>
    );
};

export default DropdownMenu;
