// frontend/src/components/AssetForm.js
import React, { useEffect, useState } from 'react';

const AssetForm = ({ onSave, lastAssetData }) => {
  const [formData, setFormData] = useState({
    actionOwner: '',
    plannedCompletionDate: '',
    actionStatus: '',
    riskStatus: '',
    riskIdentificationDate: '',
    riskAddressedDate: '',
    reviewedBy: '',
    remarks: '',
    nextReviewDate: '',
    selectedAsset: ''
  });

  const [sameAsPrevious, setSameAsPrevious] = useState(false);

  // Populate fields with lastAssetData when "Same As Previous" is checked
  useEffect(() => {
    if (sameAsPrevious && lastAssetData) {
      setFormData(lastAssetData);
    } else {
      // Clear fields if "Same As Previous" is unchecked
      setFormData({
        actionOwner: '',
        plannedCompletionDate: '',
        actionStatus: '',
        riskStatus: '',
        riskIdentificationDate: '',
        riskAddressedDate: '',
        reviewedBy: '',
        remarks: '',
        nextReviewDate: '',
        selectedAsset: ''
      });
    }
  }, [sameAsPrevious, lastAssetData]);

  // Update form data as user inputs information
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({ ...prevState, [name]: value }));
  };

  const handleAddAsset = () => {
    onSave(formData);
    // Reset form data and uncheck "Same As Previous"
    setFormData({
      actionOwner: '',
      plannedCompletionDate: '',
      actionStatus: '',
      riskStatus: '',
      riskIdentificationDate: '',
      riskAddressedDate: '',
      reviewedBy: '',
      remarks: '',
      nextReviewDate: '',
      selectedAsset: ''
    });
    setSameAsPrevious(false); // Uncheck "Same As Previous" after adding
  };

  return (
    <div className="form-container">
      {/* First Row */}
      <div className="input-group">
        <label>Action Owner</label>
        <input 
          type="text" 
          name="actionOwner" 
          value={formData.actionOwner} 
          onChange={handleInputChange} 
          placeholder="Enter Action Owner" 
        />
      </div>
      <div className="input-group">
        <label>Planned Action Completion Date</label>
        <input 
          type="date" 
          name="plannedCompletionDate" 
          value={formData.plannedCompletionDate} 
          onChange={handleInputChange} 
        />
      </div>

      {/* Additional rows... */}

      {/* Fifth Row with Next Review Date */}
      <div className="input-group">
        <label>Next Review Date</label>
        <input 
          type="date" 
          name="nextReviewDate" 
          value={formData.nextReviewDate} 
          onChange={handleInputChange} 
        />
      </div>

      {/* Row for Same As Previous Checkbox */}
      <div className="checkbox-group">
        <label>Same As Previous</label>
        <input 
          type="checkbox" 
          checked={sameAsPrevious} 
          onChange={() => setSameAsPrevious(!sameAsPrevious)} 
        />
      </div>

      {/* Add Asset Button */}
      <button className="add-asset-button" onClick={handleAddAsset}>Add Asset</button>
    </div>
  );
};

export default AssetForm;
