import React, { useState, useEffect } from 'react';
import AssetForm from './components/AssetForm';
import AssetListPanel from './components/AssetListPanel';
import './App.css';
import './index.css';

const App = () => {
    const [assets, setAssets] = useState([]);
    const [selectedAssets, setSelectedAssets] = useState([]);
    const [savedAssets, setSavedAssets] = useState([]);

    const handleAddAssets = (assetData) => {
        setSavedAssets((prev) => [...prev, ...assetData]);
        setSelectedAssets([]); // Reset selection after adding
    };

    console.log("Assets in App:", assets); // Debugging output

    return (
        <div className="App">
            <h1>Asset Selection and Management</h1>
            <AssetForm
                assets={assets}
                selectedAssets={selectedAssets}
                setSelectedAssets={setSelectedAssets}
                onAddAssets={handleAddAssets}
            />
            <AssetListPanel savedAssets={savedAssets} />
        </div>
    );
};

export default App;
