// frontend/src/components/AssetForm.js
import React, { useState } from 'react';
import { saveAsset } from '../services/apiService';

const AssetForm = ({ onSave }) => {
  const [formData, setFormData] = useState({
    actionOwner: '',
    plannedCompletionDate: '',
    actionStatus: '',
    riskStatus: '',
    riskIdentificationDate: '',
    riskAddressedDate: '',
    reviewedBy: '',
    remarks: '',
    nextReviewDate: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({ ...prevState, [name]: value }));
  };

  const handleSave = async () => {
    await saveAsset(formData); // Call API to save asset
    onSave(formData); // Update parent component’s asset list
    setFormData({ // Clear form after save
      actionOwner: '',
      plannedCompletionDate: '',
      actionStatus: '',
      riskStatus: '',
      riskIdentificationDate: '',
      riskAddressedDate: '',
      reviewedBy: '',
      remarks: '',
      nextReviewDate: ''
    });
  };

  return (
    <div className="form-container">
      <label>Action Owner</label>
      <input 
        type="text" 
        name="actionOwner" 
        value={formData.actionOwner} 
        onChange={handleInputChange} 
        placeholder="Enter Action Owner" 
      />

      <label>Planned Action Completion Date</label>
      <input 
        type="date" 
        name="plannedCompletionDate" 
        value={formData.plannedCompletionDate} 
        onChange={handleInputChange} 
      />

      <label>Action Status</label>
      <input 
        type="text" 
        name="actionStatus" 
        value={formData.actionStatus} 
        onChange={handleInputChange} 
        placeholder="Enter Action Status" 
      />

      <label>Risk Status</label>
      <input 
        type="text" 
        name="riskStatus" 
        value={formData.riskStatus} 
        onChange={handleInputChange} 
        placeholder="Enter Risk Status" 
      />

      <label>Risk Identification Date</label>
      <input 
        type="date" 
        name="riskIdentificationDate" 
        value={formData.riskIdentificationDate} 
        onChange={handleInputChange} 
      />

      <label>Risk Addressed Date</label>
      <input 
        type="date" 
        name="riskAddressedDate" 
        value={formData.riskAddressedDate} 
        onChange={handleInputChange} 
      />

      <label>Reviewed and Accepted By</label>
      <input 
        type="text" 
        name="reviewedBy" 
        value={formData.reviewedBy} 
        onChange={handleInputChange} 
        placeholder="Enter Reviewer" 
      />

      <label>Remarks</label>
      <input 
        type="text" 
        name="remarks" 
        value={formData.remarks} 
        onChange={handleInputChange} 
        placeholder="Enter Remarks" 
      />

      <label>Next Review Date</label>
      <input 
        type="date" 
        name="nextReviewDate" 
        value={formData.nextReviewDate} 
        onChange={handleInputChange} 
      />

      <button className="save-button" onClick={handleSave}>Save Asset</button>
    </div>
  );
};

export default AssetForm;
