// frontend/src/App.js
import React, { useState } from 'react';
import DropdownMenu from './components/DropdownMenu';
import AssetForm from './components/AssetForm';
import AssetListPanel from './components/AssetListPanel';
import './styles/App.css';

const App = () => {
  const [savedAssets, setSavedAssets] = useState([]);
  const [lastAssetData, setLastAssetData] = useState({});

  // Handle saving an asset temporarily
  const handleSaveAsset = () => {
    // Check if selectedAsset is defined before saving
    if (lastAssetData.selectedAsset) {
      setSavedAssets((prevAssets) => [...prevAssets, lastAssetData]);
      console.log("Saved assets:", [...savedAssets, lastAssetData]); // Debugging
    } else {
      console.warn("Asset name is missing. Please select an asset.");
    }
  };

  return (
    <div
      className="background"
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL + '/CySecAssureBG.jpeg'})`,
        backgroundSize: 'cover',
        backgroundAttachment: 'fixed',
        width: '100vw',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
      }}
    >
      <div className="overlay">
        {/* Left Panel: Dropdown and Asset Form */}
        <div className="left-panel">
          <DropdownMenu onSelectAsset={(selectedAsset) => setLastAssetData((prev) => ({ ...prev, selectedAsset }))} />
          <AssetForm onSave={handleSaveAsset} lastAssetData={lastAssetData} />
        </div>

        {/* Right Panel: Saved Assets */}
        <div className="right-panel">
          <AssetListPanel savedAssets={savedAssets} />
        </div>
      </div>

      {/* Bottom row for buttons: Only one instance of buttons */}
      <div className="button-row">
        <button className="add-asset-button" onClick={handleSaveAsset}>Add Asset</button>
        <button className="generate-report-button">Generate Report</button>
      </div>
    </div>
  );
};

export default App;
