// frontend/src/components/AssetForm.js
import React, { useState } from 'react';
import DropdownMenu from './DropdownMenu';

const AssetForm = ({ assets, onAddAssets }) => {
    const [selectedAssets, setSelectedAssets] = useState([]);
    const [actionOwner, setActionOwner] = useState('');
    const [riskIdentificationDate, setRiskIdentificationDate] = useState('');

    const handleAddAssets = () => {
        if (selectedAssets.length === 0 || !actionOwner || !riskIdentificationDate) {
            console.warn("Ensure all fields and assets are selected.");
            return;
        }

        const assetData = selectedAssets.map((asset) => ({
            assetName: asset,
            actionOwner,
            riskIdentificationDate,
            existingControls: [] // Initialize empty for each asset
        }));

        console.log("Adding assets with data:", assetData);
        onAddAssets(assetData);

        setSelectedAssets([]); // Clear selection after adding
    };

    const handleSelectAsset = (assetName) => {
        setSelectedAssets((prev) =>
            prev.includes(assetName)
                ? prev.filter((asset) => asset !== assetName)
                : [...prev, assetName]
        );
    };

    return (
        <div className="asset-form">
            <DropdownMenu
                assets={assets}
                selectedAssets={selectedAssets}
                onSelectAsset={handleSelectAsset}
            />
            <div>
                <label>Action Owner:</label>
                <input
                    type="text"
                    value={actionOwner}
                    onChange={(e) => setActionOwner(e.target.value)}
                />
            </div>
            <div>
                <label>Risk Identification Date:</label>
                <input
                    type="date"
                    value={riskIdentificationDate}
                    onChange={(e) => setRiskIdentificationDate(e.target.value)}
                />
            </div>
            <button onClick={handleAddAssets}>Add Asset</button>
        </div>
    );
};

export default AssetForm;
