// frontend/src/components/DropdownMenu.js
import React, { useEffect, useState } from 'react';
import { fetchCollectionsWithAssets } from '../services/apiService';

const DropdownMenu = ({ onSelectAsset }) => {
  const [collections, setCollections] = useState([]);

  useEffect(() => {
    const getCollections = async () => {
      try {
        const data = await fetchCollectionsWithAssets();
        console.log("Fetched collections with assets:", data); // Debug log for API response
        setCollections(data || []); // Ensure an empty array if data is undefined
      } catch (error) {
        console.error("Error fetching collections with assets:", error);
      }
    };
    getCollections();
  }, []);

  return (
    <div className="dropdown">
      <label>Select Asset</label>
      <select className="dropdown-select" onChange={(e) => onSelectAsset(e.target.value)}>
        <option value="">-- Select an Asset --</option>
        {collections.map((collection) => (
          <optgroup label={collection.name} key={collection.name}>
            {collection.assets.map((asset) => (
              <option key={asset} value={asset}>{asset}</option>
            ))}
          </optgroup>
        ))}
      </select>
    </div>
  );
};

export default DropdownMenu;
