// frontend/src/components/DropdownMenu.js
import React, { useEffect, useState } from 'react';
import { fetchCollections } from '../services/apiService';

const DropdownMenu = () => {
  const [collections, setCollections] = useState([]);

  useEffect(() => {
    const getCollections = async () => {
      try {
        const data = await fetchCollections();
        // Filter out SecurityControls and set collections
        setCollections(data.filter(collection => collection.name !== 'SecurityControls'));
      } catch (error) {
        console.error("Error fetching collections:", error);
      }
    };
    getCollections();
  }, []);

  return (
    <div className="dropdown">
      <label>Select Asset</label>
      <select>
        {collections.map(collection => (
          <optgroup label={collection.name} key={collection._id}>
            {collection.assets.map(asset => (
              <option key={asset._id} value={asset.name}>{asset.name}</option>
            ))}
          </optgroup>
        ))}
      </select>
    </div>
  );
};

export default DropdownMenu;
