// frontend/src/App.js
import React, { useState } from 'react';
import DropdownMenu from './components/DropdownMenu';
import AssetForm from './components/AssetForm';
import AssetListPanel from './components/AssetListPanel';
import './styles/App.css';

const App = () => {
  const [savedAssets, setSavedAssets] = useState([]);
  const [lastAssetData, setLastAssetData] = useState({});
  const [selectedAsset, setSelectedAsset] = useState('Select an Asset'); // Set default dropdown value

  const handleSaveAsset = (assetData) => {
    if (assetData.selectedAsset && assetData.selectedAsset !== 'Select an Asset') {
      setSavedAssets(prevAssets => [...prevAssets, assetData]);
      setLastAssetData(assetData); // Save data for reuse
      setSelectedAsset('Select an Asset'); // Reset dropdown to default
      console.log("Temporary stored data (savedAssets):", [...savedAssets, assetData]);
    } else {
      console.warn("Please select a valid asset.");
    }
  };

  return (
    <div
      className="background"
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL + '/CySecAssureBG.jpeg'})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        backgroundAttachment: 'fixed',
        width: '100vw',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
      }}
    >
      <div className="overlay">
        <div className="left-panel">
          <DropdownMenu 
            selectedAsset={selectedAsset} 
            onSelectAsset={(selected) => setSelectedAsset(selected)} 
          />
          <AssetForm
            onSave={handleSaveAsset}
            lastAssetData={lastAssetData} // Pass last saved asset data
            selectedAsset={selectedAsset} // Pass current dropdown selection
          />
          <div className="button-container">
            <button className="add-asset-button" onClick={() => handleSaveAsset({
              ...lastAssetData, selectedAsset
            })}>
              Add Asset
            </button>
          </div>
        </div>

        <div className="right-panel">
          <AssetListPanel savedAssets={savedAssets} />
          <div className="generate-report-container">
            <button className="generate-report-button">Generate Report</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
