// frontend/src/App.js
import React, { useState } from 'react';
import DropdownMenu from './components/DropdownMenu';
import AssetForm from './components/AssetForm';
import AssetListPanel from './components/AssetListPanel';
import './styles/App.css';

const App = () => {
    const [savedAssets, setSavedAssets] = useState([]);
    const [lastAssetData, setLastAssetData] = useState({});
    const [selectedAsset, setSelectedAsset] = useState('Select an Asset');

    const handleSaveAsset = (assetData) => {
        if (assetData.selectedAsset && assetData.selectedAsset !== 'Select an Asset') {
            setSavedAssets(prevAssets => [...prevAssets, assetData]);
            setLastAssetData(assetData);
            setSelectedAsset('Select an Asset');
            console.log("Temporary stored data (savedAssets):", [...savedAssets, assetData]);
        } else {
            console.warn("Please select a valid asset.");
        }
    };

    const handleGenerateReport = async () => {
        console.log("Generate Report button clicked");  // Confirm function is triggered

        try {
            const response = await fetch('http://localhost:5000/api/reports/generate-report', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    assetList: savedAssets.map(asset => asset.selectedAsset),
                    temporaryData: savedAssets.reduce((acc, asset) => {
                        acc[asset.selectedAsset] = asset;
                        return acc;
                    }, {}),
                }),
            });

            console.log("Response received:", response);
            const result = await response.json();
            console.log("Parsed result:", result);

            if (response.ok) {
                console.log("Opening report URL:", result.reportUrl);
                window.open(result.reportUrl, '_blank');
            } else {
                console.error('Failed to generate report:', result.message);
            }
        } catch (error) {
            console.error('Error generating report:', error);
        }
    };

    return (
        <div
            className="background"
            style={{
                backgroundImage: `url(${process.env.PUBLIC_URL + '/CySecAssureBG.jpeg'})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                backgroundRepeat: 'no-repeat',
                backgroundAttachment: 'fixed',
                width: '100vw',
                height: '100vh',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                overflow: 'hidden',
            }}
        >
            <div className="overlay">
                <div className="left-panel">
                    <DropdownMenu 
                        selectedAsset={selectedAsset} 
                        onSelectAsset={(selected) => setSelectedAsset(selected)} 
                    />
                    <AssetForm
                        onSave={handleSaveAsset}
                        lastAssetData={lastAssetData}
                        selectedAsset={selectedAsset}
                    />
                </div>

                <div className="right-panel">
                    <AssetListPanel savedAssets={savedAssets} />
                    <div className="generate-report-container">
                        <button onClick={handleGenerateReport} className="generate-report-button">
                            Generate Report
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default App;
