// frontend/src/App.js
import React, { useState } from 'react';
import DropdownMenu from './components/DropdownMenu';
import AssetForm from './components/AssetForm';
import AssetListPanel from './components/AssetListPanel';
import './styles/App.css';

const App = () => {
  const [savedAssets, setSavedAssets] = useState([]);
  const [lastAssetData, setLastAssetData] = useState({});

  const handleSaveAsset = (assetData) => {
    if (assetData.selectedAsset) {
      setSavedAssets(prevAssets => [...prevAssets, assetData]);
      setLastAssetData(assetData); // Update the last asset data
    } else {
      console.warn("Asset name is missing. Please select an asset.");
    }
  };

  const generateReport = () => {
    console.log("Report generated for saved assets:", savedAssets);
    // Add actual report generation logic here
  };

  return (
    <div
      className="background"
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL + '/CySecAssureBG.jpeg'})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        backgroundAttachment: 'fixed',
        width: '100vw',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
      }}
    >
      <div className="overlay">
        <div className="left-panel">
          <DropdownMenu onSelectAsset={(selectedAsset) => setLastAssetData(prev => ({ ...prev, selectedAsset }))} />
          <AssetForm
            onSave={handleSaveAsset}
            lastAssetData={lastAssetData} // Pass last asset data to AssetForm
          />
          <div className="button-container">
            <button className="add-asset-button" onClick={() => handleSaveAsset(lastAssetData)}>Add Asset</button>
          </div>
        </div>

        <div className="right-panel">
          <AssetListPanel savedAssets={savedAssets} />
          <div className="generate-report-container">
            <button className="generate-report-button" onClick={generateReport}>Generate Report</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
