// frontend/src/components/AssetListPanel.js
import React, { useState } from 'react';
import ExistingControlsPopup from './popups/ExistingControlsPopup';

const AssetListPanel = ({ savedAssets, onUpdateAssetControls }) => {
    const [selectedAsset, setSelectedAsset] = useState(null);

    const openPopup = (asset) => setSelectedAsset(asset);
    const closePopup = () => setSelectedAsset(null);

    const handleSaveControls = (controls) => {
        console.log(`Selected controls for ${selectedAsset.assetName}:`, controls);
        onUpdateAssetControls(selectedAsset.assetName, controls);
        closePopup();
    };

    return (
        <div className="asset-list-panel">
            <h3>Saved Assets</h3>
            {savedAssets.map((asset, index) => (
                <div key={index} onClick={() => openPopup(asset)}>
                    <p>{asset.assetName}</p>
                    <p>Action Owner: {asset.actionOwner}</p>
                    <p>Risk Identification Date: {asset.riskIdentificationDate}</p>
                    <p>Existing Controls: {asset.existingControls.join(', ')}</p>
                </div>
            ))}
            {selectedAsset && (
                <ExistingControlsPopup
                    assetName={selectedAsset.assetName}
                    onClose={closePopup}
                    onSave={handleSaveControls}
                />
            )}
        </div>
    );
};

export default AssetListPanel;
