// frontend/src/components/DropdownMenu.js
import React, { useState } from 'react';

const DropdownMenu = ({ assets = [], selectedAssets, onSelectAsset }) => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleDropdown = () => setIsOpen(!isOpen);

    const handleAssetClick = (assetName) => {
        onSelectAsset(assetName);
    };

    return (
        <div className="dropdown-menu">
            <button onClick={toggleDropdown}>Select Assets</button>
            {isOpen && (
                <div className="dropdown-list">
                    {/* Check that assets is defined and is an array before mapping */}
                    {Array.isArray(assets) && assets.length > 0 ? (
                        assets.map((asset) => (
                            <div key={asset} className="dropdown-item">
                                <input
                                    type="checkbox"
                                    checked={selectedAssets.includes(asset)}
                                    onChange={() => handleAssetClick(asset)}
                                />
                                <label>{asset}</label>
                            </div>
                        ))
                    ) : (
                        <p>No assets available</p>
                    )}
                </div>
            )}
        </div>
    );
};

export default DropdownMenu;
