import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

// Fetch all collections with associated assets
export const fetchCollectionsWithAssets = async () => {
    try {
        console.log("Attempting to fetch collections with assets..."); // Debugging output
        const response = await axios.get(`${API_BASE_URL}/collections-with-assets`);
        console.log("Fetched collections with assets:", response.data); // Log response data
        return response.data;
    } catch (error) {
        console.error("Error fetching collections with assets:", error);
        return [];
    }
};

// Fetch existing controls for a specific asset by asset name
export const fetchControlsForAsset = async (assetName) => {
    try {
        const encodedAssetName = encodeURIComponent(assetName); // Encode asset name to handle spaces/special chars
        console.log(`Attempting to fetch controls for asset: ${assetName}`); // Debugging output

        const response = await axios.get(`${API_BASE_URL}/assets/${encodedAssetName}/controls`);

        console.log(`Response from backend for controls of ${assetName}:`, response.data); // Log response data
        return response.data.controls; // Return controls as an array
    } catch (error) {
        console.error(`Error fetching controls for asset ${assetName}:`, error);
        return [];
    }
};

// Save a new asset with its details to the backend
export const saveAsset = async (data) => {
    try {
        console.log("Attempting to save asset data:", data); // Debugging output
        await axios.post(`${API_BASE_URL}/save-asset`, data);
        console.log("Asset saved successfully!"); // Confirmation log
        alert("Asset saved successfully!");
    } catch (error) {
        console.error("Error saving asset:", error);
    }
};

// Fetch all saved assets for display
export const fetchSavedAssets = async () => {
    try {
        console.log("Attempting to fetch saved assets..."); // Debugging output
        const response = await axios.get(`${API_BASE_URL}/saved-assets`);
        console.log("Fetched saved assets:", response.data); // Log response data
        return response.data;
    } catch (error) {
        console.error("Error fetching saved assets:", error);
        return [];
    }
};
