// frontend/src/App.js
import React, { useState } from 'react';
import DropdownMenu from './components/DropdownMenu';
import AssetForm from './components/AssetForm';
import AssetListPanel from './components/AssetListPanel';
import './styles/App.css';

const App = () => {
  const [savedAssets, setSavedAssets] = useState([]);
  const [lastAssetData, setLastAssetData] = useState({});
  const [selectedAsset, setSelectedAsset] = useState('Select an Asset'); // Default dropdown value

  const handleSaveAsset = (assetData) => {
    if (assetData.selectedAsset && assetData.selectedAsset !== 'Select an Asset') {
      setSavedAssets(prevAssets => [...prevAssets, assetData]);
      setLastAssetData(assetData); // Save data for reuse
      setSelectedAsset('Select an Asset'); // Reset dropdown to default
      console.log("Temporary stored data (savedAssets):", [...savedAssets, assetData]);
    } else {
      console.warn("Please select a valid asset.");
    }
  };

  // Function to handle Generate Report
  const handleGenerateReport = async () => {
    try {
      // Send POST request to backend to generate the report
      const response = await fetch('/api/reports/generate-report', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          assetList: savedAssets.map(asset => asset.selectedAsset), // Array of selected asset names
          temporaryData: savedAssets.reduce((acc, asset) => {
            acc[asset.selectedAsset] = asset; // Map asset data by selectedAsset name
            return acc;
          }, {}),
        }),
      });

      const result = await response.json();

      if (response.ok) {
        // Open the generated report in a new tab
        window.open(result.reportUrl, '_blank');
      } else {
        console.error('Failed to generate report:', result.message);
      }
    } catch (error) {
      console.error('Error generating report:', error);
    }
  };

  return (
    <div
      className="background"
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL + '/CySecAssureBG.jpeg'})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        backgroundAttachment: 'fixed',
        width: '100vw',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'hidden',
      }}
    >
      <div className="overlay">
        <div className="left-panel">
          <DropdownMenu 
            selectedAsset={selectedAsset} 
            onSelectAsset={(selected) => setSelectedAsset(selected)} 
          />
          <AssetForm
            onSave={handleSaveAsset}
            lastAssetData={lastAssetData} // Pass last saved asset data
            selectedAsset={selectedAsset} // Pass current dropdown selection
          />
        </div>

        <div className="right-panel">
          <AssetListPanel savedAssets={savedAssets} />
          <div className="generate-report-container">
            <button onClick={handleGenerateReport} className="generate-report-button">
              Generate Report
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
